import 'package:flutter/material.dart';

/// ðŸŽ¨ Diagonal zigzag background pattern matching the design
class ZigzagBackground extends StatelessWidget {
  final Widget child;
  final Color backgroundColor;
  final Color lineColor;
  final double lineWidth;
  final double zigzagHeight;

  const ZigzagBackground({
    super.key,
    required this.child,
    this.backgroundColor = const Color(0xFF0B0B0B),
    this.lineColor = const Color(0xFF1A1A1A),
    this.lineWidth = 1.2,
    this.zigzagHeight = 12.0,
  });

  @override
  Widget build(BuildContext context) {
    return Stack(
      children: [
        // Background with zigzag pattern
        Positioned.fill(
          child: CustomPaint(
            painter: ZigzagPainter(
              backgroundColor: backgroundColor,
              lineColor: lineColor,
              lineWidth: lineWidth,
              zigzagHeight: zigzagHeight,
            ),
          ),
        ),
        // Content on top
        child,
      ],
    );
  }
}

class ZigzagPainter extends CustomPainter {
  final Color backgroundColor;
  final Color lineColor;
  final double lineWidth;
  final double zigzagHeight;

  ZigzagPainter({
    required this.backgroundColor,
    required this.lineColor,
    required this.lineWidth,
    required this.zigzagHeight,
  });

  @override
  void paint(Canvas canvas, Size size) {
    // Draw background
    canvas.drawRect(
      Rect.fromLTWH(0, 0, size.width, size.height),
      Paint()..color = backgroundColor,
    );

    // Draw diagonal zigzag lines
    final Paint linePaint = Paint()
      ..color = lineColor
      ..strokeWidth = lineWidth
      ..strokeCap = StrokeCap.round
      ..style = PaintingStyle.stroke;

    const double spacing = 20.0; // Distance between zigzag lines

    // Draw diagonal zigzag pattern
    double xStart = -size.height;
    while (xStart < size.width) {
      final Path path = Path();
      double x = xStart;
      double y = 0;

      path.moveTo(x, y);

      // Draw zigzag pattern going diagonally
      while (x < size.width && y < size.height) {
        // Draw a zigzag segment
        x += spacing / 2;
        y += zigzagHeight / 2;
        path.lineTo(x, y);

        x += spacing / 2;
        y -= zigzagHeight / 2;
        path.lineTo(x, y);
      }

      canvas.drawPath(path, linePaint);
      xStart += spacing;
    }
  }

  @override
  bool shouldRepaint(ZigzagPainter oldDelegate) {
    return oldDelegate.backgroundColor != backgroundColor ||
        oldDelegate.lineColor != lineColor ||
        oldDelegate.lineWidth != lineWidth ||
        oldDelegate.zigzagHeight != zigzagHeight;
  }
}
