import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:google_fonts/google_fonts.dart';
import 'package:hotspot_onboarding/features/experience_selection/data/experience_model.dart';
import 'package:hotspot_onboarding/features/experience_selection/provider/experience_provider.dart';
import 'package:hotspot_onboarding/features/experience_selection/presentation/widgets/experience_card.dart';
import 'package:hotspot_onboarding/features/onboarding_question/presentation/onboarding_question_screen.dart';

class ExperienceSelectionScreen extends ConsumerWidget {
  const ExperienceSelectionScreen({super.key});

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final selectionState = ref.watch(experienceSelectionProvider);
    final service = ref.read(experienceServiceProvider);

    return Scaffold(
      backgroundColor: Colors.white,
      appBar: AppBar(
        title: Text(
          'Select Your Experience',
          style: GoogleFonts.inter(
              color: Colors.black, fontWeight: FontWeight.w600),
        ),
        backgroundColor: Colors.white,
        elevation: 0,
        centerTitle: true,
      ),
      body: FutureBuilder<List<ExperienceModel>>(
        future: service.fetchExperiences(),
        builder: (context, snapshot) {
          if (snapshot.connectionState == ConnectionState.waiting) {
            return const Center(child: CircularProgressIndicator());
          }

          if (!snapshot.hasData || snapshot.data!.isEmpty) {
            return const Center(child: Text('No experiences found.'));
          }

          final experiences = snapshot.data!;

          return Padding(
            padding: const EdgeInsets.symmetric(horizontal: 16, vertical: 12),
            child: Column(
              children: [
                Expanded(
                  child: GridView.builder(
                    itemCount: experiences.length,
                    gridDelegate:
                        const SliverGridDelegateWithFixedCrossAxisCount(
                      crossAxisCount: 2,
                      crossAxisSpacing: 12,
                      mainAxisSpacing: 12,
                      childAspectRatio: 0.8,
                    ),
                    itemBuilder: (context, index) {
                      final exp = experiences[index];
                      final isSelected =
                          selectionState.selectedIds.contains(exp.id);
                      return ExperienceCard(
                        experience: exp,
                        isSelected: isSelected,
                        onTap: () => ref
                            .read(experienceSelectionProvider.notifier)
                            .toggleSelection(exp.id),
                      );
                    },
                  ),
                ),
                const SizedBox(height: 20),
                TextField(
                  maxLength: 250,
                  maxLines: 4,
                  decoration: InputDecoration(
                    hintText: 'Tell us more about your experience...',
                    border: OutlineInputBorder(
                      borderRadius: BorderRadius.circular(12),
                    ),
                  ),
                  onChanged: (val) => ref
                      .read(experienceSelectionProvider.notifier)
                      .setDescription(val),
                ),
                const SizedBox(height: 20),
                ElevatedButton(
                  style: ElevatedButton.styleFrom(
                    backgroundColor: Colors.black,
                    shape: RoundedRectangleBorder(
                      borderRadius: BorderRadius.circular(12),
                    ),
                    minimumSize: const Size(double.infinity, 50),
                  ),
                  onPressed: () {
                    print(
                        'Selected IDs: ${selectionState.selectedIds}, Text: ${selectionState.description}');
                    Navigator.push(
                      context,
                      MaterialPageRoute(
                        builder: (context) =>
                            const OnboardingQuestionScreen(),
                      ),
                    );
                  },
                  child: const Text('Next',
                      style: TextStyle(color: Colors.white, fontSize: 16)),
                ),
              ],
            ),
          );
        },
      ),
    );
  }
}
