import 'package:flutter/material.dart';

class RecordVideoButton extends StatelessWidget {
  final VoidCallback onRecord;
  final VoidCallback? onDelete;
  final bool hasRecording;

  const RecordVideoButton({
    super.key,
    required this.onRecord,
    this.onDelete,
    required this.hasRecording,
  });

  @override
  Widget build(BuildContext context) {
    return AnimatedContainer(
      duration: const Duration(milliseconds: 400),
      curve: Curves.easeInOut,
      width: hasRecording ? 70 : 150,
      height: 50,
      child: ElevatedButton.icon(
        onPressed: hasRecording ? onDelete : onRecord,
        icon: Icon(hasRecording ? Icons.delete : Icons.videocam),
        label: hasRecording
            ? const Text('Delete')
            : const Text('Record Video'),
        style: ElevatedButton.styleFrom(
          backgroundColor: hasRecording ? Colors.red : Colors.black,
          shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(12)),
        ),
      ),
    );
  }
}
